using UnityEngine;using System.Collections;public class Movement2 : MonoBehaviour {	[SerializeField]	Animator myanimator;	public float moveSpeed;	private Rigidbody myRigidbody;	private Vector3 moveInput;	private Vector3 moveVelocity;	private Camera Camera;	public GunControl theGun;	// Use this for initialization	void Start () {		myRigidbody = GetComponent<Rigidbody> ();		Camera = FindObjectOfType<Camera> ();	}	// Update is called once per frame	void Update () {		moveInput = new Vector3 (Input.GetAxisRaw ("Horizontal"), 0f, Input.GetAxisRaw ("Vertical"));		moveVelocity = moveInput * moveSpeed;		Ray cameraRay = Camera.ScreenPointToRay (Input.mousePosition);		Plane groundplane = new Plane (Vector3.up, Vector3.zero);		float rayLength;		if (groundplane.Raycast (cameraRay, out rayLength)) {			Vector3 pointToLook = cameraRay.GetPoint (rayLength);			Debug.DrawLine (cameraRay.origin, pointToLook, Color.blue);			transform.LookAt (new Vector3(pointToLook.x, transform.position.y, pointToLook.z));		}		if (Input.GetMouseButtonDown (0)) {			theGun.isFiring = true;		}		if (Input.GetMouseButtonUp (0)) {			theGun.isFiring = false;		}	}	void FixedUpdate(){		myRigidbody.velocity = moveVelocity;		if (myRigidbody.velocity.magnitude > 0) {			myanimator.SetBool ("Moving",true);		} else {			myanimator.SetBool ("Moving",false);		}	}}